package week1.day1;

public class EmployeeDetails {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Declare and initialize the variables
		
		String name = "John Doe";
		byte age = 30;
		float salary = 55000.75f;
		char gender = 'M';
		boolean acceptTerms = true;
		float rating = 4.5f;
		long mobileNumber = 9876543210L;
		int pinCode = 56001;
		byte luckyNumber = 7;
		
		//Print all the variables
		
		System.out.println("****Employee Details***");
		System.out.println("Name: "+name+"\n"+"Age: "+age+"\n"+"Salary: "+salary+"\n"+"Gender: "+gender);
		System.out.println("Accept Terms: "+acceptTerms+"Rating: "+rating+"\nMobile Number: "+mobileNumber);
		System.out.println("Pin Code: "+pinCode+"\nLucky Number: "+luckyNumber);
	}

}
